msg.topic = "UPDATE active_task " +
    "SET status = 'Finished_Treated' " +
    "WHERE bpmn_id = '" + msg.activeTask.bpmn_id + "' " +
    "AND instance_id = " + msg.activeTask.instance_id +
    ";";
return msg;

//get all sequence flows within the xml
var arAllSequenceFlows = msg.recipe.definitions.process[0].sequenceFlow;

//get sequenceFlows linked to the current taks
var arActiveTaskSequenceFlows = arAllSequenceFlows.filter(x => x.$.sourceRef == msg.activeTask.bpmn_id);

if(! arActiveTaskSequenceFlows){
    msg.message = "No sequenceFlows found for current task «" + msg.activeTask.bpmn_id + "»";
    return;
}

//camunda: the sequence flows of exlusiveGateways should have conditionExpressions, need to loop and evaluate
var conditionExpressionBoolResult;
var selectedSequenceFlow;
var conditionExpression;

if(msg.implementationKind == "camunda"){
    for (var i = 0; i < arActiveTaskSequenceFlows.length; i++) {
        selectedSequenceFlow  = arActiveTaskSequenceFlows[i];
        if (!selectedSequenceFlow.conditionExpression){
            msg.message = "Error, conditionExpression has not been set to the exclusiveGateway «" + msg.activeTask.bpmn_id + "»";
            return msg;
        }

        //get condition expression of current sequenceFlow
        conditionExpression = selectedSequenceFlow.conditionExpression[0]["_"];

        //replace characters not recognized by JS
        conditionExpression = conditionExpression.replace("$","").replace("{","").replace("}","");

        //replace variable name by the actual value given by «msg.serviceResponse». if evaluating a string, put \" to the value
        if (conditionExpression.includes("\"")) 
            conditionExpression = conditionExpression.replace(msg.serviceResponseParameterName, "\""+msg.serviceResponse+"\"");
        else
            conditionExpression = conditionExpression.replace(msg.serviceResponseParameterName, msg.serviceResponse);

        //evaluate conditionExpression. Select the sequenceFlow that gets true as eval result
        try {
            conditionExpressionBoolResult = eval(conditionExpression);
            if (Boolean(conditionExpressionBoolResult)) break;
        } catch (error) {
            msg.message = error.message;
            conditionExpressionBoolResult = false;
            break;
        };
    }
}else{
    msg.message = "Error, sorry to inform that evaluation of conditionExpressions are supported only for Camunda";
    return msg;
}

msg.conditionExpression = conditionExpression;
msg.conditionExpressionBoolResult = conditionExpressionBoolResult;
msg.selectedSequenceFlow = selectedSequenceFlow;
return msg;

//get next taskBpmnId by getting targetRef value of the current sequenceFlow
var nextTaskBpmnId = msg.selectedSequenceFlow.$.targetRef;
var nextTaskObj;

//find the next task to be executed
var processObj = msg.recipe.definitions.process[0];
for (let processEntry in processObj) {
    //the first entry «$» is not a task. dont consider it
    if (processEntry == '$') continue;

    nextTaskObj = processObj[processEntry].find(x => x.$.id == nextTaskBpmnId);
    if (nextTaskObj){
        //set type
        nextTaskObj.$.type = processEntry;
        break;
    }    
}

if (!nextTaskObj){
    msg.message = "Error, task «" + nextTaskBpmnId + "» not found";
    return msg;
}

msg.nextTaskObj = nextTaskObj;
return msg;

//get all intermediateCatchEvents of the whole process
var arIntermediateCatchEvents = msg.recipe.definitions.process[0].intermediateCatchEvent;

//validate recipe should contain at least one ServiceTask
if (!arIntermediateCatchEvents || arIntermediateCatchEvents.length == 0) {
    msg.message = "Error, recipe does not cotain any intermediateCatchEvent task";
    return msg;
}

//get the intermediateCatchEvent to be executed
var intermediateCatchEventXmlObj = arIntermediateCatchEvents.find(x => x.$.id == msg.activeTask.bpmn_id);
if (!intermediateCatchEventXmlObj) {
    msg.message = "Error, intermediateCatchEvent task «" + msg.activeTask.bpmn_id + "» not found";
    return msg;
}

//get intermediateCatchEvent's messageRef attribute value
msg.intermediateCatchEventMessageRef = intermediateCatchEventXmlObj.messageEventDefinition[0].$.messageRef;
return msg;

//get all serviceTasks
var arServiceTasks = msg.recipe.definitions.process[0].serviceTask;

//validate recipe should contain at least one ServiceTask
if (!arServiceTasks || arServiceTasks.length == 0){
    msg.message = "Error, recipe does not cotain any service task";
    return msg;
}

//get the serviceTask to be executed
var serviceTaskXmlObj = arServiceTasks.find(x => x.$.id == msg.activeTask.bpmn_id);
if (!serviceTaskXmlObj){
    msg.message = "Error, service task «" + msg.activeTask.bpmn_id + "» not found";
    return msg;
}

//get and set ServiceTask's property values based on implementation kind
var serviceTaskIsAsync = false;
var serviceTaskUrl = "", serviceTaskMethod = "";

switch (msg.implementationKind){
    case "camunda":
        serviceTaskIsAsync = serviceTaskXmlObj.$["camunda:asyncBefore"] == "true";
        serviceTaskUrl = serviceTaskXmlObj.extensionElements[0]["camunda:connector"][0]["camunda:inputOutput"][0]["camunda:inputParameter"].find(x => x.$.name == "url")["_"];
        serviceTaskMethod = serviceTaskXmlObj.extensionElements[0]["camunda:connector"][0]["camunda:inputOutput"][0]["camunda:inputParameter"].find(x => x.$.name == "method")["_"];
        break;
    case "activiti":
        serviceTaskIsAsync = serviceTaskXmlObj.$["activiti:async"] == "true";
        serviceTaskUrl = serviceTaskXmlObj.extensionElements[0]["activiti:field"].find(x => x.$.name == "serviceURL")["activiti:string"][0];
        serviceTaskMethod = serviceTaskXmlObj.extensionElements[0]["activiti:field"].find(x => x.$.name == "method")["activiti:string"][0];
        break;
    default:
        break;
}

msg.serviceTaskXmlObj = serviceTaskXmlObj;
msg.serviceTaskIsAsync = serviceTaskIsAsync;
msg.serviceTaskUrl = serviceTaskUrl;
msg.serviceTaskMethod = serviceTaskMethod;
return msg;

var outputParameterName;
var serviceResponse = [200, 201, 202].includes(msg.statusCode) ? msg.payload : "null";

switch (msg.implementationKind){
    case "camunda":
        outputParameterName = msg.serviceTaskXmlObj.extensionElements[0]["camunda:connector"][0]["camunda:inputOutput"][0]["camunda:outputParameter"][0].$.name;
        break;
    case "activiti":
        outputParameterName = msg.serviceTaskXmlObj.$.id + "Response";
        break;
    default:
        break;
}

msg.serviceResponseParameterName = outputParameterName;
msg.serviceResponse = serviceResponse;
msg.serviceResponseDebugMessage = {
    taskBpmnId: msg.serviceTaskXmlObj.$.id,
    url: msg.serviceTaskUrl,
    replacedBy: msg.contextValidation?.result?.suggestedService?? null,
    statusCode: msg.statusCode,
    response: msg.serviceResponse
};
return msg;

//QoS could come with the recipe under the <serviceTask> element or in the request of start_process
//This time is just for experimental reasons
